import React from 'react';
import { useDispatch } from 'react-redux';
import { Button, Card, List, Comment, Popover } from 'antd';
import { StarOutlined, EllipsisOutlined, InstagramOutlined, MessageOutlined } from '@ant-design/icons';

import CommentForm from './CommentForm';

const ProductionCard = () => {
  const dispatch = useDispatch();
  return (
    <div>
      <Card
        style={{ width: 300 }}
        actions={[
          <StarOutlined key="star" />,
          <InstagramOutlined key="instagram" />,
          <MessageOutlined key="comment" />,
          <Popover
            key="more"
            content={[
              <Button.Group>
                <Button>수정</Button>
                <Button type="danger">삭제</Button>
                <Button>신고</Button>
              </Button.Group>,
            ]}
          >
            <EllipsisOutlined />
          </Popover>,
        ]}
      >
        제품
      </Card>
      {commentFormOpened && (
        <>
          <CommentForm />
          <List
            itemLayout="horizontal"
            renderItem={(item) => (
              <li>
                <Comment />
              </li>
            )}
          />
        </>
      )}
    </div>
  );
};

export default ProductionCard;
