import React from 'react';
import { Form, Input, Button } from 'antd';

const CommentForm = () => (
  <Form>
    <Form.Item style={{ position: 'relative', margin: 0 }}>
      <Input.TextArea rows={4} />
      <Button
        style={{ position: 'absolute', right: 0, bottom: -40, zIndex: 1 }}
        type="primary"
        htmlType="submit"
      >
        댓글
      </Button>
    </Form.Item>
  </Form>
);

export default CommentForm;
