import React from 'react';
import PropTypes from 'prop-types';
import Link from 'next/link';
import SignUpModal from './SignUpModal';
// import Router from 'next/router';
// import styled from 'styled-components';

import { Menu, Input, Row, Col, Typography } from 'antd';
import LoginModal from './LoginModal';
import { useSelector } from 'react-redux';

const AppLayout = ({ children }) => {
  const { me } = useSelector((state) => state.user);

  return (
    <div>
      <Menu mode="horizontal">
        <Menu.Item>
          <Link href="/"><Typography strong="true"><a>거래자들</a></Typography></Link>
        </Menu.Item>
        {/* 모바일에선 햄버거 버튼이 나오게 회원가입 로그인 검색 줄이기 */}
        <Menu.Item>
          <Input.Search enterButton style={{ verticalAlign: 'middle' }} size="large" />
        </Menu.Item>
        {me && <Menu.Item style={{ float: 'right' }}><SignUpModal /></Menu.Item>}
        {/* 로그인시 회원가입 버튼 없애고 xxx님 어서오세요. 내 정보 보기 등등 뜨게 */}
        <Menu.Item style={{ float: 'right' }}>
          <LoginModal />
        </Menu.Item>
      </Menu>
      <Row gutter={8}>
        <Col xs={24} md={4}>
          <Menu mode="inline">
            <Menu.Item>전체보기</Menu.Item>
            <Menu.Item>디지털/가전</Menu.Item>
            <Menu.Item>의류</Menu.Item>
            <Menu.Item>생활용품</Menu.Item>
            <Menu.Item>기타</Menu.Item>
          </Menu>
        </Col>
        <Col xs={24} md={20}>{children}</Col>
      </Row>
    </div>
  );
};

AppLayout.propTypes = {
  children: PropTypes.node.isRequired,
};

export default AppLayout;
