import React, { useCallback, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import Link from 'next/link';
import { List, Comment, Avatar, Button } from 'antd';
import PropTypes from 'prop-types';
import Recomment from './Recomment';
import { REMOVE_COMMENT_REQUEST } from '../reducers/product';

const Comments = ({ comment }) => {
  const id = useSelector((state) => state.user.me?.id);
  const dispatch = useDispatch();
  const [commentId, setCommentId] = useState('');

  const onRemoveComment = useCallback(() => {
    if (!id) {
      return alert('로그인이 필요합니다.');
    }
    return dispatch({
      type: REMOVE_COMMENT_REQUEST,
      data: commentId,
    });
  }, [id, commentId]);

  return (
    <List
      style={{ width: 400 }}
      header={`${comment.length}개의 댓글`}
      itemLayout="horizontal"
      dataSource={comment}
      renderItem={(item) => (
        <List.Item key={item.id}>
          <Comment
            author={item.User.nickname}
            avatar={(
              <>
                <Link href={`/user/${item.User.id}`}>
                  <a><Avatar>{item.User.nickname[0]}</Avatar></a>
                </Link>
                {id && item.User.id === id
                  ? (
                    <Button danger="true" onClick={onRemoveComment}>
                      삭제
                      {setCommentId(item)}
                    </Button>
                  ) : ''}
                <Recomment comment={item} />
              </>
            )}
            content={item.content}
          />
        </List.Item>
      )}
    />
  );
};

Comments.propTypes = {
  comment: PropTypes.arrayOf(PropTypes.object).isRequired,
};

export default Comments;
