import React, { useCallback, useState } from 'react';
import PropTypes from 'prop-types';
import { useDispatch, useSelector } from 'react-redux';
import Link from 'next/link';
import { Button, Card, List, Popover, Avatar, Comment } from 'antd';
import { StarOutlined, EllipsisOutlined, InstagramOutlined, MessageOutlined, StarTwoTone } from '@ant-design/icons';

import CommentForm from './CommentForm';
import ProductContent from './ProductContent';
import ProductImages from './ProductImages';
import Recomment from './Recomment';
import RecommentForm from './RecommentForm';
import { LIKE_PRODUCT_REQUEST, UNLIKE_PRODUCT_REQUEST, REMOVE_PRODUCT_REQUEST } from '../reducers/product';

const ProductCard = ({ product }) => {
  const dispatch = useDispatch();
  const { me } = useSelector((state) => state.user);
  const id = me && me.id;
  const [commentFormOpened, setCommentFormOpened] = useState(false);
  const [recommentOpen, setRecommentOpen] = useState(false);

  const onRemoveProduct = useCallback(() => {
    if (!id) {
      return alert('로그인이 필요합니다.');
    }
    return dispatch({
      type: REMOVE_PRODUCT_REQUEST,
      data: product.id,
    });
  }, [id]);

  const onLike = useCallback(() => {
    if (!id) {
      return alert('로그인이 필요합니다.');
    }
    return dispatch({
      type: LIKE_PRODUCT_REQUEST,
      data: product.id,
    });
  }, [id]);

  const onUnlike = useCallback(() => {
    if (!id) {
      return alert('로그인이 필요합니다.');
    }
    return dispatch({
      type: UNLIKE_PRODUCT_REQUEST,
      data: product.id,
    });
  }, [id]);

  const recommentFormOpen = useCallback(() => {
    setRecommentOpen((prev) => !prev);
  }, []);

  const onToggleComment = useCallback(() => {
    setCommentFormOpened((prev) => !prev);
  }, []);

  const liked = product.Favored.find((v) => v.id === id);
  return (
    <div style={{ margin: '7px' }}>
      <Card
        cover={product.Images[0] && <ProductImages images={product.Images} />}
        style={{ width: 400 }}
        hoverable="true"
        actions={[
          liked
            ? <StarTwoTone key="star" twoToneColor="gold" onClick={onUnlike} />
            : <StarOutlined key="star" onClick={onLike} />,
          <InstagramOutlined key="instar" />,
          <MessageOutlined key="comment" onClick={onToggleComment} />,
          <Popover
            key="more"
            content={(
              <Button.Group>
                {id ? (
                  <>
                    <Button>수정</Button>
                    <Button type="danger" onClick={onRemoveProduct}>삭제</Button>
                  </>
                ) : <Button>신고</Button>}
              </Button.Group>
            )}
          >
            <EllipsisOutlined key="ellips" />
          </Popover>,
        ]}
      >
        <>
          <div style={{ float: 'right' }}>{product.category}</div>
          <Card.Meta
            avatar={(
              <Link href={`/user/${product.User.id}`}>
                <a><Avatar>{product.User.nickname[0]}</Avatar></a>
              </Link>
            )}
            title={product.User.nickname}
            description={<ProductContent productContent={product.content} />}
          />
        </>
      </Card>
      {commentFormOpened && (
        <>
          <CommentForm product={product} />
          <List
            style={{ width: 400 }}
            header={`${product.Comments.length}개의 댓글`}
            itemLayout="horizontal"
            dataSource={product.Comments}
            renderItem={(item) => (
              <List.Item key={item.id}>
                <Comment
                  author={item.User.nickname}
                  avatar={(
                    <>
                      <Link href={`/user/${item.User.id}`}>
                        <a><Avatar>{item.User.nickname[0]}</Avatar></a>
                      </Link>
                      <Button onClick={recommentFormOpen}>답글</Button>
                      {recommentOpen && <RecommentForm comment={item.id} />}
                    </>
                  )}
                  content={item.content}
                >
                  <Recomment comment={product.Comments} />
                </Comment>
              </List.Item>
            )}
          />
        </>
      )}
    </div>
  );
};

ProductCard.propTypes = {
  product: PropTypes.shape({
    id: PropTypes.number,
    User: PropTypes.object,
    content: PropTypes.string,
    category: PropTypes.string,
    createAt: PropTypes.string,
    Comments: PropTypes.arrayOf(PropTypes.object),
    Recomments: PropTypes.arrayOf(PropTypes.object),
    Images: PropTypes.arrayOf(PropTypes.any),
    Favored: PropTypes.arrayOf(PropTypes.object),
  }).isRequired,
};

export default ProductCard;
