  // }, [hasMoreProducts, loadProductsLoading, mainProducts]);

  useEffect(() => {
    dispatch({
      type: LOAD_PRODUCTS_REQUEST,
    });
  }, []);
  return (
    <AppLayout>
      {mainProducts.map((product) => (
        <ProductCard
          style={{ width: 300 }}
          key={product.id}
          product={product}
        />
      ))}
    </AppLayout>
  );
};

export default Home;
