import React, { useState, useCallback } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import Link from 'next/link';
import { List, Comment, Avatar, Button } from 'antd';
import PropTypes from 'prop-types';
import Recomment from './Recomment';
import RecommentForm from './RecommentForm';
import { REMOVE_COMMENT_REQUEST } from '../reducers/product';

const Comments = ({ comment }) => {
  const id = useSelector((state) => state.user.me?.id);
  const dispatch = useDispatch();
  // const [commentId, setCommentId] = useState('');
  const [recommentOpen, setRecommentOpen] = useState(false);
  const recommentFormOpen = useCallback(() => {
    setRecommentOpen((prev) => !prev);
  }, []);

  const onRemoveComment = (item) => () => {
    if (!id) {
      return alert('로그인이 필요합니다.');
    }
    return dispatch({
      type: REMOVE_COMMENT_REQUEST,
      data: item,
    });
  };

  return (
    <List
      header={`${comment.length}개의 댓글`}
      itemLayout="horizontal"
      locale={{ emptyText: '댓글을 작성해주세요.' }}
      dataSource={comment}
      renderItem={(item) => (
        <List.Item key={item.id}>
          <Comment
            author={item.User.nickname}
            content={item.content}
            avatar={(
              <>
                <Link href={`/user/${item.User.id}`}>
                  <a><Avatar>{item.User.nickname[0]}</Avatar></a>
                </Link>
                <div style={{ position: 'absolute', right: -300, top: 0 }}>
                  {/* {id && item.User.id === id
                    ? (
                      <>
                        <Button danger="true" onClick={onRemoveComment(item)}>
                          삭제
                        </Button>
                        <br />
                      </>
                    ) : ''} */}
                  <Button onClick={recommentFormOpen}>답글</Button>
                  {recommentOpen && <RecommentForm comment={item} />}
                </div>
                <Recomment comment={item} />
              </>
            )}
          />
        </List.Item>
      )}
    />
  );
};

Comments.propTypes = {
  comment: PropTypes.arrayOf(PropTypes.object).isRequired,
};

export default Comments;
