import React from 'react';
import { List, Comment, Avatar } from 'antd';
import PropTypes from 'prop-types';

const Recomment = () => (
  <List
    style={{ width: 300 }}
    itemLayout="horizontal"
    // dataSource={comment.Recomments}
    renderItem={(initem) => (
      <li>
        <Comment
          author={initem.User.nickname}
          avatar={(
            <Avatar>{initem.User.nickname[0]}</Avatar>
          )}
          content={initem.content}
        />
      </li>
    )}
  />
);

// Recomment.propTypes = {
//   comment: PropTypes.objectOf.isRequired,
// };

export default Recomment;
