import React, { useCallback, useEffect } from 'react';
import { Button, Form, Input, Select } from 'antd';
import { useDispatch, useSelector } from 'react-redux';
import useInput from '../hooks/useInput';
import { ADD_PRODUCT_REQUEST } from '../reducers/product';

const ProductionForm = () => {
  const { imagePaths, addProductDone } = useSelector((state) => state.product);
  const dispatch = useDispatch();
  const [text, onChangeText, setText] = useInput('');

  useEffect(() => {
    if (addProductDone) {
      setText('');
    }
  }, [addProductDone]);

  const onSubmit = useCallback(() => {
    if (!text || !text.trim()) {
      return alert('내용을 작성하세요.');
    }
    const formData = new FormData();
    imagePaths.forEach((p) => {
      formData.append('image', p);
    });
    formData.append('content', text);
    return dispatch({
      type: ADD_PRODUCT_REQUEST,
      data: formData,
    });
  }, [text, imagePaths]);
  return (
    <>
      <Form style={{ margin: '10px 0 20px' }} encType="nultipart/form-data" onFinish={onSubmit}>
        <Select defaultValue="category">
          <Select.Option value="digital">디지털/가전</Select.Option>
          <Select.Option value="cloths">의류</Select.Option>
          <Select.Option value="life">생활용품</Select.Option>
          <Select.Option value="other">기타</Select.Option>
        </Select>
        <div style={{ marginBottom: '10px' }}>
          <input type="file" name="image" multiple hidden />
          <Button>상품 이미지 업로드</Button>
          <br />
        </div>
        <Input.TextArea placeholder="내용입력" value={text} onChange={onChangeText} />
        <Button type="primary" style={{ float: 'right' }} htmlType="submit">저장</Button>
      </Form>
    </>
  );
};

export default ProductionForm;
