import React, { useCallback, useState } from 'react';
import { Button, Card, List, Comment, Popover } from 'antd';
import { StarOutlined, EllipsisOutlined, InstagramOutlined, MessageOutlined } from '@ant-design/icons';

import CommentForm from './CommentForm';
import ProductionContent from './ProductionContent';

const ProductionCard = () => {
  const [commentFormOpened, setCommentFormOpened] = useState(false);

  const onToggleComment = useCallback(() => {
    setCommentFormOpened((prev) => !prev);
  });

  return (
    <div>
      <Card
        style={{ width: 300 }}
        actions={[
          <StarOutlined key="star" />,
          <InstagramOutlined key="instar" />,
          <MessageOutlined key="comment" onClick={onToggleComment} />,
          <Popover
            key="more"
            content={[
              <Button.Group>
                <>
                  <Button>수정</Button>
                  <Button type="danger">삭제</Button>
                  <Button>신고</Button>
                </>
              </Button.Group>,
            ]}
          >
            <EllipsisOutlined />
          </Popover>,
        ]}
      >
        <Card.Meta
          title="title"
          description={<ProductionContent />}
        />
      </Card>
      {commentFormOpened && (
        <>
          <CommentForm />
          <List
            itemLayout="horizontal"
            // renderItem={(item) => (
            //   <li>
            //     <Comment />
            //   </li>
            // )}
          />
        </>
      )}
    </div>
  );
};

export default ProductionCard;
