import React, { useState, useCallback } from 'react';
import { Button, List, Comment, Avatar } from 'antd';
import PropTypes from 'prop-types';

import RecommentForm from './RecommentForm';

const Recomment = ({ comment }) => {
  console.log(comment);
  const [recommentOpen, setRecommentOpen] = useState(false);

  const recommentFormOpen = useCallback(() => {
    setRecommentOpen((prev) => !prev);
  }, []);
  return (
    <>
      <Button onClick={recommentFormOpen}>답글</Button>
      {recommentOpen && <RecommentForm comment={comment} />}
      <List
        style={{ width: 300 }}
        itemLayout="horizontal"
        dataSource={comment.Recomments}
        renderItem={(initem) => (
          <li>
            <Comment
              author={initem.User.nickname}
              avatar={(
                <Avatar>{initem.User.nickname[0]}</Avatar>
              )}
              content={initem.content}
            />
          </li>
        )}
      />
    </>
  );
};

Recomment.propTypes = {
  comment: PropTypes.shape({
    Recomments: PropTypes.arrayOf(PropTypes.object),
  }).isRequired,
};

export default Recomment;
