import React, { useCallback, useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import Router from 'next/router';
import Head from 'next/head';
import { Form, Input, Button, Checkbox } from 'antd';

import useInput from '../hooks/useInput';
import { SIGN_UP_REQUEST } from '../reducers/user';

const SignUpModal = () => {
  const dispatch = useDispatch();
  const { signUpLoading, signUpDone, signUpError, me } = useSelector((state) => state.user);
  const [email, onChangeEmail] = useInput('');
  const [nickname, onChangeNickname] = useInput('');
  const [password, onChangePassword] = useInput('');

  useEffect(() => {
    if (me && me.id) {
      Router.replace('/');
    }
  }, [me && me.id]);

  useEffect(() => {
    if (signUpDone) {
      Router.replace('/');
    }
  }, [signUpDone]);

  useEffect(() => {
    if (signUpError) {
      alert(signUpError);
    }
  }, [signUpError]);

  const [phonenumber, setPhonenumber] = useState('');
  const [phonenumberError, setPhonenumberError] = useState(false);

  // 정규표현식 번호 검사 다시 구현 서버에서도 구현
  const regExp = /^01(?:0|1[6-9])[.-]?(\\d{3}|\\d{4})[.-]?(\\d{4})$/;
  const onChangePhonenumber = useCallback((e) => {
    setPhonenumber(e.target.value);
    setPhonenumberError(false);
  }, []);

  const [passwordCheck, setPasswordCheck] = useState('');
  const [passwordError, setPasswordError] = useState(false);

  const onChangePasswordCheck = useCallback((e) => {
    setPasswordCheck(e.target.value);
    setPasswordError(e.target.value !== password);
  }, [password]);

  const [agree, setAgree] = useState('');
  const [agreeError, setAgreeError] = useState(false);

  const onChangeAgree = useCallback((e) => {
    setAgree(e.target.checked);
    setAgreeError(false);
  }, []);

  const onSubmit = useCallback(() => {
    if (password !== passwordCheck) {
      return setPasswordError(true);
    }
    if (!agree) {
      return setAgreeError(true);
    }
    if (!(regExp.test(phonenumber))) {
      return setPhonenumberError(true);
    }
    return dispatch({
      type: SIGN_UP_REQUEST,
      data: { email, nickname, phonenumber, password },
    });
  }, [email, nickname, phonenumber, password, passwordCheck]);

  return (
    <>
      <Head>
        <title>회원가입 | 거래자들</title>
      </Head>
      <div>
        <h2>회원가입</h2>
        <Form onFinish={onSubmit}>
          <div>
            <label htmlFor="user-email">이메일</label>
            <br />
            <Input name="user-email" type="email" value={email} required onChange={onChangeEmail} />
          </div>
          <div>
            <label htmlFor="user-nickname">닉네임</label>
            <br />
            <Input name="user-nickname" value={nickname} required onChange={onChangeNickname} />
          </div>
          <div>
            <label htmlFor="user-email">휴대폰 번호</label>
            <br />
            <Input name="user-email" type="text" value={phonenumber} required onChange={onChangePhonenumber} />
            {phonenumberError && <span>번호를 다시 입력하세요.</span>}
          </div>
          <div>
            <label htmlFor="user-password">비밀번호</label>
            <br />
            <Input name="user-password" type="password" value={password} required onChange={onChangePassword} />
          </div>
          <div>
            <label htmlFor="user-password-check">비밀번호 확인</label>
            <br />
            <Input name="user-password-check" type="password" value={passwordCheck} required onChange={onChangePasswordCheck} />
            {passwordError && <span>비밀번호가 일치하지 않습니다.</span>}
          </div>
          <div>
            <Checkbox name="user-term" checked={agree} onChange={onChangeAgree}>동의합니다.</Checkbox>
            {agreeError && <span>약관에 동의하셔야 합니다.</span>}
          </div>
          <div style={{ marginTop: 10 }}>
            <Button type="primary" htmlType="submit" loading={signUpLoading}>회원가입</Button>
          </div>
        </Form>
      </div>
    </>
  );
};

export default SignUpModal;
