import React from 'react';
import PropTypes from 'prop-types';
import { PlusOutlined } from '@ant-design/icons';
import styled from 'styled-components';

const ImageWrapper = styled.div`
  width: 400px;
  height: 300px;
`;
const NoneImageText = styled.span`
  position: absolute;
  top: 10;
`;

const ProductImages = ({ images }) => {
  if (images.length === 0) {
    return (
      <>
        <ImageWrapper>
          <NoneImageText>거래자들</NoneImageText>
        </ImageWrapper>
      </>
    );
  }
  if (images.length === 1) {
    return (
      <>
        <img role="presentation" src={`http://localhost:3080/${images[0].src}`} alt={images[0].src} />
        {/* {showImageZoom && <ImagesZoom images={images} />} */}
      </>
    );
  }
  return (
    <>
      <div>
        <img role="presentation" style={{ width: '50%' }} src={`http://localhost:3080/${images[0].src}`} alt={images[0].src} />
        <div
          role="presentation"
          style={{ display: 'inline-block', width: '50%', textAlign: 'center', verticalAlign: 'middle' }}
        >
          <PlusOutlined />
          <br />
          {images.length - 1}
          개의 사진 더보기
        </div>
      </div>
    </>
  );
};

ProductImages.propTypes = {
  images: PropTypes.arrayOf(PropTypes.shape({
    src: PropTypes.string,
  })).isRequired,
};

export default ProductImages;
